import React, { useState } from "react";
import axios from "axios";
import UserContext from "./UserContext";

const API = axios.create({
  baseURL: "https://fitnesstrack-vtv1.onrender.com/api/",
});

const UserState = (props) => {
  const [user, setUser] = useState(null);
  const [dashboard, setDashboard] = useState(null);
  const [workouts, setWorkouts] = useState([]);

  // User Sign-Up
  const UserSignUp = async (data) => {
    try {
      const response = await API.post("user/signup", data);
      setUser(response.data);
      return response.data;
    } catch (error) {
      console.error("Error during sign-up:", error.response?.data || error.message);
      throw error;
    }
  };

  // User Sign-In
  const UserSignIn = async (data) => {
    try {
      const response = await API.post("user/signin", data);
      setUser(response.data);
      return response.data;
    } catch (error) {
      console.error("Error during sign-in:", error.response?.data || error.message);
      throw error;
    }
  };

  // Get Dashboard Details
  const getDashboardDetails = async (token) => {
    try {
      const response = await API.get("user/dashboard", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setDashboard(response.data);
      return response.data;
    } catch (error) {
      console.error("Error fetching dashboard details:", error.response?.data || error.message);
      throw error;
    }
  };

  // Get Workouts
  const getWorkouts = async (token, date) => {
    try {
      const response = await API.get(`user/workout?date=${date}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setWorkouts(response.data);
      return response.data;
    } catch (error) {
      console.error("Error fetching workouts:", error.response?.data || error.message);
      throw error;
    }
  };

  // Add Workout
  const addWorkout = async (token, data) => {
    try {
      const response = await API.post("user/workout", data, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setWorkouts([...workouts, response.data]);
      return response.data;
    } catch (error) {
      console.error("Error adding workout:", error.response?.data || error.message);
      throw error;
    }
  };

  return (
    <UserContext.Provider
      value={{ user, dashboard, workouts, UserSignUp, UserSignIn, getDashboardDetails, getWorkouts, addWorkout }}
    >
      {props.children}
    </UserContext.Provider>
  );
};

export default UserState;
